import React, { useState } from 'react';

function Folder({ name, content, deleteItem, addItem }) {
  const [isOpen, setIsOpen] = useState(false);
  const [newItemName, setNewItemName] = useState('');

  const toggleFolder = () => {
    setIsOpen(!isOpen);
  };

  const handleAddItem = () => {
    if (newItemName.trim() !== '') {
      addItem(newItemName, name);
      setNewItemName('');
    }
  };

  return (
    <div className="folder">
      <div onClick={toggleFolder}>
        {isOpen ? '▼' : '▶'} {name}
      </div>
      {isOpen && (
        <div>
          {content.map((item) => (
            <div key={item}>
              {typeof item === 'string' ? (
                <span>{item}</span>
              ) : (
                <Folder name={Object.keys(item)[0]} content={item[Object.keys(item)[0]]} deleteItem={deleteItem} addItem={addItem} />
              )}
              <button onClick={() => deleteItem(item, name)}>Delete</button>
            </div>
          ))}
          <input
            type="text"
            placeholder="Enter item name"
            value={newItemName}
            onChange={(e) => setNewItemName(e.target.value)}
          />
          <button onClick={handleAddItem}>Add Item</button>
        </div>
      )}
    </div>
  );
}

export default Folder;
