import React, { useState } from 'react';

const Folder = ({ data, onEditItem, onDelete, depth }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [newItemName, setNewItemName] = useState('');
  const [editedItemName, setEditedItemName] = useState('');
  const [isAdding, setIsAdding] = useState(false);
  const [isAddingFolder, setIsAddingFolder] = useState(false);

  const toggleFolder = () => {
    setIsOpen(!isOpen);
  };


  const handleEditClick = (itemName) => {
    const newName = prompt('Enter the new name:', itemName);
    if (newName !== null) {
      setEditedItemName(newName); // Update the edited item name
      onEditItem(itemName, newName);
    }
  };

  const handleAddFolder = (newFolderName) => {
    if (newFolderName.trim() !== '') {
      const newFolder = {
        name: newFolderName,
        content: [],
      };
      setData([...data, newFolder]);
    }
  };
  

  const handleDeleteItem = (itemName) => {
    const confirmDelete = window.confirm(`Are you sure you want to delete ${itemName}?`);
    if (confirmDelete) {
      const index = data.content.indexOf(itemName);
      if (index !== -1) {
        data.content.splice(index, 1);
        onDelete(itemName);
      }
    }
  };

  return (
    <div className='folder'>
      <div onClick={toggleFolder}>
        {isOpen ? 'üìÇ ' : 'üìÅ '} {editedItemName || data.name} {/* Use editedItemName if available */}
      </div>
      <div style={{ marginLeft: `${(depth + 1) * 20}px` }}>
        {isOpen && (
          <div>
            {data.content.map((item, index) => (
              <div key={index}>
                {typeof item === 'object' ? (
                  <Folder data={item} onEditItem={onEditItem} onDelete={onDelete} depth={depth + 1} />
                ) : (
                  <div style={{ display: 'flex', alignItems: 'center' }}>
                    <span>{item}</span>
                    <div className='folder-btn'>
                      <button style={{ marginLeft: '20px', marginRight: '5px' }} onClick={() => handleEditClick(item)}>Edit</button>
                      <button onClick={() => handleDeleteItem(item)}>Delete</button>
                    </div>
                  </div>
                )}
              </div>
            ))}
            {isAdding && (
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <input
                  type="text"
                  placeholder="Enter item name"
                  value={newItemName}
                  onChange={(e) => setNewItemName(e.target.value)}
                />
                <button style={{ marginLeft: '10px' }} onClick={handleAddFolder}>Add Item</button>
              </div>
            )}
            {!isAdding && (
              <button style={{ marginLeft: '10px' }} onClick={() => setIsAdding(true)}>Add Item</button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Folder;
