import React, { useState } from 'react';
import Folder from './Folder';
import AddForm from './AddForm';

const initialData = [
    {
      name: "Applications",
      content: [
        "Webstorm.dmg",
        "Pycharm.dmg",
        "FileZila.dmg",
        "Mattermost.dmg",
      ],
    },
    {
      name: "Desktop",
      content: [
        "Screenshot1.jpg",
        "videopal.mp4"
      ],
    },
    {
      name: "Documents",
      content: [
        "Document1.jpg",
        "Document2.jpg",
        "Document3.jpg"
      ],
    },
    {
      name: "Downloads",
      content: [
        {
          name: "Drivers",
          content: [
            "Printerdriver.dmg",
            "cameradriver.dmg"
          ],
        },
      ],
    },
    {
      name: "Images",
      content: [],
    },
    {
      name: "chromedriver.dmg",
      content: null,
    },
  ];

function App() {
  const [data, setData] = useState(initialData);

  const handleEditItem = (folderName, itemName, newItemName) => {
    const updatedData = data.map((item) => {
      if (item.name === folderName) {
        const updatedContent = item.content.map((contentItem) => {
          if (contentItem === itemName) {
            return newItemName;
          }
          return contentItem;
        });
        return { ...item, content: updatedContent };
      }
      return item;
    });
    setData(updatedData);
  };

  const handleDeleteItem = (folderName, itemName) => {
    const updatedData = data.map((item) => {
      if (item.name === folderName) {
        const updatedContent = item.content.filter((contentItem) => contentItem !== itemName);
        return { ...item, content: updatedContent };
      }
      return item;
    });
    setData(updatedData);
  };

  const handleAddFolder = (parentFolderName, newFolderName) => {
    const newFolder = {
      name: newFolderName, 
      content: [], 
    };
  
    // Update the state to add the new folder to the parent folder
    const updatedData = data.map((item) => {
      if (item.name === parentFolderName) {
        return {
          ...item,
          content: [...item.content, newFolder],
        };
      }
      return item;
    });
  
    setData(updatedData);
  };
  
 

  const handleAddFile = (folderName, newFileName) => {
    const updatedData = data.map((item) => {
      if (item.name === folderName) {
        return { ...item, content: [...item.content, newFileName] };
      }
      return item;
    });
    setData(updatedData);
  };

  return (
    <div>
      <h1>Folder Structure</h1>
      <div className="top-bar">
        <div className="top-bar-buttons">
        <AddForm onAddItem={handleAddFolder} data={data} />
          
        </div>
        {data.map((item) => (
          <Folder
            key={item.name}
            data={item}
            onEditItem={handleEditItem}
            onDelete={handleDeleteItem}
            onAddItem={handleAddFolder}
            onAddFile={handleAddFile}
            depth={0}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
