import React, { useState } from 'react';
import Folder from './Folder';
import AddForm from './AddForm';

const initialData = [
  {
    name: "Applications",
    content: [
      "Webstorm.dmg",
      "Pycharm.dmg",
      "FileZila.dmg",
      "Mattermost.dmg",
    ],
  },
  {
    name: "Desktop",
    content: [
      "Screenshot1.jpg",
      "videopal.mp4"
    ],
  },
  {
    name: "Documents",
    content: [
      "Document1.jpg",
      "Document2.jpg",
      "Document3.jpg"
    ],
  },
  {
    name: "Downloads",
    content: [
      {
        name: "Drivers",
        content: [
          "Printerdriver.dmg",
          "cameradriver.dmg"
        ],
      },
    ],
  },
  {
    name: "Images",
    content: [],
  },
  {
    name: "chromedriver.dmg",
    content: null,
  },
];

function FolderStructure() {
  const [data, setData] = useState(initialData);

  const handleEditItem = (itemName, newName) => {
    const newData = [...data];
    const itemToEdit = newData.find((item) => item.name === itemName);
    if (itemToEdit) {
      itemToEdit.name = newName;
      setData(newData);
    }
  };

  const handleDeleteItem = (itemName) => {
    const newData = [...data];
    const index = newData.findIndex((item) => item.name === itemName);
    if (index !== -1) {
      newData.splice(index, 1);
      setData(newData);
    }
  };

  const handleAddItem = (newItemName) => {
    if (newItemName.trim() !== '') {
      const newItem = {
        name: newItemName,
        content: [],
      };
      setData([...data, newItem]);
    }
  };

  return (
    <div >
      <h1>Folder Structure</h1>
      <div className="folder-structure">
        <AddForm onAddItem={handleAddItem} />
        {data.map((item) => (
            <Folder
            key={item.name}
            data={item}
            onEditItem={handleEditItem}
            onDelete={handleDeleteItem}
            onAddItem={handleAddItem}
            depth={0}
            />
        ))}
      </div>
     
    </div>
  );
}

export default FolderStructure;
