import React, { useState } from 'react';

const Folder = ({ data, onEditItem, onDelete, depth }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [newItemName, setNewItemName] = useState('');
  const [isAdding, setIsAdding] = useState(false);

  const toggleFolder = () => {
    setIsOpen(!isOpen);
  };

  const handleEditClick = (itemName) => {
    const newName = prompt('Enter the new name:', itemName);
    if (newName !== null) {
      onEditItem(itemName, newName);
    }
  };

  const handleAddItem = () => {
    if (newItemName.trim() !== '') {
      data.content.push(newItemName);
      setNewItemName('');
      setIsAdding(false);
    }
  };

  const handleDeleteItem = (itemName) => {
    const confirmDelete = window.confirm(`Are you sure you want to delete ${itemName}?`);
    if (confirmDelete) {
      const index = data.content.indexOf(itemName);
      if (index !== -1) {
        data.content.splice(index, 1);
      }
      onDelete(itemName);
    }
  };

  return (
    <div className={`folder ${isOpen ? 'open' : ''}`} style={{ marginLeft: `${depth * 20}px` }}>
      <div className="folder-content" onClick={toggleFolder}>
        <span className="folder-icon">{isOpen ? 'üìÇ' : 'üìÅ'}</span> {data.name}
        <button className="edit-button" onClick={() => handleEditClick(data.name)}>Edit</button>
        <button className="delete-button" onClick={() => handleDeleteItem(data.name)}>Delete</button>
      </div>
      {isOpen && (
        <div>
          {data.content.map((item, index) => (
            <div key={index} className="folder-item" style={{ marginLeft: `${(depth + 1) * 20}px` }}>
              {typeof item === 'object' ? (
                <Folder data={item} onEditItem={onEditItem} onDelete={onDelete} depth={depth + 1} />
              ) : (
                <div>
                  {item}
                  <button className="edit-button" onClick={() => handleEditClick(item)}>Edit</button>
                  <button className="delete-button" onClick={() => handleDeleteItem(item)}>Delete</button>
                </div>
              )}
            </div>
          ))}
          {isAdding && (
            <div className="add-item" style={{ marginLeft: `${(depth + 1) * 20}px` }}>
              <input
                type="text"
                placeholder="Enter item name"
                value={newItemName}
                onChange={(e) => setNewItemName(e.target.value)}
              />
              <button className="add-button" onClick={handleAddItem}>Add Item</button>
            </div>
          )}
          {!isAdding && (
            <button className="add-button" onClick={() => setIsAdding(true)}>Add Item</button>
          )}
        </div>
      )}
    </div>
  );
};

export default Folder;
