import React, { useState } from 'react';
import Folder from './Folder';
import AddForm from './AddForm';

const initialData = {
  Applications: [
    "Webstorm.dmg",
    "Pycharm.dmg",
    "FileZila.dmg",
    "Mattermost.dmg"
  ],
  Desktop: [
    "Screenshot1.jpg",
    "videopal.mp4"
  ],
  Documents: [
    "Document1.jpg",
    "Document2.jpg",
    "Document3.jpg"
  ],
  Downloads: {
    Drivers: [
      "Printerdriver.dmg",
      "cameradriver.dmg"
    ],
    Images: [],
    "chromedriver.dmg": null
  }
};

function FolderStructure() {
  const [data, setData] = useState(initialData);

  const addItem = (itemName, parentFolder) => {
    const newData = { ...data };

    if (parentFolder === 'Root') {
      newData[itemName] = [];
    } else if (newData[parentFolder] instanceof Array) {
      newData[parentFolder].push(itemName);
    } else {
      newData[parentFolder][itemName] = [];
    }

    setData(newData);
  };

  const deleteItem = (itemName, parentFolder) => {
    const newData = { ...data };

    if (newData[parentFolder] instanceof Array) {
      const index = newData[parentFolder].indexOf(itemName);
      if (index !== -1) {
        newData[parentFolder].splice(index, 1);
      }
    } else {
      delete newData[parentFolder][itemName];
    }

    setData(newData);
  };

  return (
    <div className="folder-structure">
      <Folder name="Root" content={data} deleteItem={deleteItem} addItem={addItem} />
      <AddForm addItem={addItem} />
    </div>
  );
}

export default FolderStructure;
