// FolderStructure.js
import React from 'react';
import Folder
 from './Folder';

const FolderOrFile = ({ item, onAddFile, onAddFolder, onDelete, depth }) => {
  if (Array.isArray(item)) {
    return (
      <>
        {item.map((subItem) => (
          <FolderOrFile
            key={subItem}
            item={subItem}
            onAddFile={onAddFile}
            onAddFolder={onAddFolder}
            onDelete={onDelete}
            depth={depth}
          />
        ))}
      </>
    );
  } else if (typeof item === 'string') {
    return <File key={item} name={item} onDelete={onDelete} depth={depth} />;
  } else if (typeof item === 'object') {
    const folderName = Object.keys(item)[0];
    const folderContents = item[folderName];
    return (
      <Folder
        key={folderName}
        name={folderName}
        children={folderContents}
        onToggle={onAddFolder}
        onAddFile={onAddFile}
        onAddFolder={onAddFolder}
        onDelete={onDelete}
        depth={depth}
      />
    );
  }
};

const FolderStructure = ({ data, onAddFile, onAddFolder, onDelete }) => {
  return (
    <div>
      {data.map((item) => (
        <FolderOrFile
          key={item}
          item={item}
          onAddFile={onAddFile}
          onAddFolder={onAddFolder}
          onDelete={onDelete}
          depth={0}
        />
      ))}
    </div>
  );
};

export default FolderStructure;
