import React, { useState } from 'react';

function FileStructure({ name, deleteItem, isFolderOpen, onEditFile, onAddFile }) {
  const [newFileName, setNewFileName] = useState('');
  const [isEditing, setIsEditing] = useState(false);

  const handleEditFile = () => {
    if (newFileName.trim() !== '') {
      onEditFile(name, newFileName);
      setIsEditing(false);
    }
  };

  const handleAddFile = () => {
    if (newFileName.trim() !== '') {
      onAddFile(newFileName);
      setNewFileName('');
    }
  };

  return (
    <div>
      {isEditing ? (
        <div>
          <input
            type="text"
            placeholder="Enter new file name"
            value={newFileName}
            onChange={(e) => setNewFileName(e.target.value)}
          />
          <button onClick={handleEditFile}>Update</button>
        </div>
      ) : (
        <div>
          <span>ðŸ“„{name}</span>
          <button onClick={() => deleteItem(name)}>Delete</button>
          <button onClick={() => setIsEditing(true)}>Edit</button>
        </div>
      )}
      {!isFolderOpen && (
        <div>
          <input
            type="text"
            placeholder="Add new file"
            value={newFileName}
            onChange={(e) => setNewFileName(e.target.value)}
          />
          <button onClick={handleAddFile}>Add File</button>
        </div>
      )}
    </div>
  );
}

export default FileStructure;
