import React, { useState } from 'react';
import Folder from './Folder';
import AddForm from './AddForm';

const initialData = [
  {
    Documents: ['Document1.jpg', 'Document2.jpg', 'Document3.jpg'],
    Desktop: ['Screenshot1.jpg', 'videopal.mp4'],
    Downloads: [
      {
        Drivers: ['Printerdriver.dmg', 'cameradriver.dmg'],
      },
      'chromedriver.dmg',
    ],
    Applications: ['Webstorm.dmg', 'Pycharm.dmg', 'FileZila.dmg', 'Mattermost.dmg'],
  },
];

function FolderStructure() {
  const [data, setData] = useState(initialData);

  //Create
  const addItem = (itemName, parentFolder) => {
    const newData = [...data];
    const parent = newData.find((folder) => folder[parentFolder]);

    if (parent) {
      if (!parent[parentFolder].includes(itemName)) {
        parent[parentFolder].push(itemName);
        setData(newData); 
      }
    }
  };

  //Delete 
  const deleteItem = (itemName, parentFolder) => {
    const newData = [...data];
    const parent = newData.find((folder) => folder[parentFolder]);

    if (parent) {
      const index = parent[parentFolder].indexOf(itemName);
      if (index !== -1) {
        parent[parentFolder].splice(index, 1);
        setData(newData); 
      }
    }
  };

  return (
    <div className="folder-structure">
      <Folder name="Root" content={data} deleteItem={deleteItem} addItem={addItem} />
      <AddForm addItem={addItem} />
    </div>
  );
}

export default FolderStructure;
