import React, { useState } from 'react';
import Folder from './components/Folder';
import AddForm from './components/AddForm';

const initialData = [
  {
    name: "Documents",
    content: [
      "Document1.txt",
      "Document2.txt",
    ],
  },
  {
    name: "Pictures",
    content: [
      {
        name: "Vacation",
        content: [
          "Beach.jpg",
          "Mountain.jpg",
        ],
      },
    ],
  },
  {
    name: "Music",
    content: [
      "Song1.mp3",
      "Song2.mp3",
    ],
  },
];

function App() {
  const [data, setData] = useState(initialData);

  const handleEditItem = (folderName, newName) => {
    const updatedData = data.map((item) => {
      if (item.name === folderName) {
        return { ...item, name: newName };
      }
      return item;
    });
    setData(updatedData);
  };

  const handleDeleteItem = (folderName) => {
    const updatedData = data.filter((item) => item.name !== folderName);
    setData(updatedData);
  };

  const handleAddFolder = (parentFolder, newFolderName) => {
    const updatedData = data.map((item) => {
      if (item.name === parentFolder) {
        return {
          ...item,
          content: [...item.content, { name: newFolderName, content: [] }],
        };
      }
      return item;
    });
    setData(updatedData);
  };

  const handleAddFile = (folderName, newFileName) => {
    const updatedData = data.map((item) => {
      if (item.name === folderName) {
        return { ...item, content: [...item.content, newFileName] };
      }
      return item;
    });
    setData(updatedData);
  };

  return (
    <div>
      <h1>Folder Structure</h1>
      <div className="top-bar">
        <div className="top-bar-buttons">
          <AddForm onAddItem={handleAddFolder} />
        </div>
        {data.map((item) => (
          <Folder
            key={item.name}
            data={item}
            onEditItem={handleEditItem}
            onDelete={handleDeleteItem}
            onAddItem={handleAddFolder}
            onAddFile={handleAddFile}
            depth={0}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
